def q_greedify_policy(env, V, pi, s, gamma):
    """Mutate ``pi`` to be greedy with respect to the q-values induced by ``V``."""
    # YOUR CODE HERE
    action=env.A
    G=np.zeros_like(action,dtype=float)
    for a in action:
        transit=env.transitions(s,a)
        for s_,(rew,prob) in enumerate(transit):
            G[a]+=prob*(rew+gamma*V[s_])
    all_greedy=np.argwhere(G == np.amax(G))
    for a in action:
        if a in all_greedy:
            pi[s,a]=1/len(all_greedy)
        else:
            pi[s,a]=0
